import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, 
  Play, 
  Pause, 
  Settings, 
  Palette,
  Eraser,
  RotateCcw,
  Users,
  MessageCircle,
  Timer
} from 'lucide-react';
import { motion } from 'framer-motion';
import toast from 'react-hot-toast';
import useAuthStore from '../store/authStore';
import useGameStore from '../store/gameStore';
import socketManager from '../utils/socket';
import DrawingCanvas from '../components/DrawingCanvas';
import ChatBox from '../components/ChatBox';
import PlayerList from '../components/PlayerList';
import Button from '../components/Button';

const GameRoom = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const { user, token } = useAuthStore();
  const {
    currentRoom,
    players,
    isInRoom,
    isGameActive,
    currentRound,
    totalRounds,
    timeRemaining,
    currentWord,
    isDrawing,
    currentDrawer,
    brushColor,
    brushSize,
    isDrawingMode,
    setBrushColor,
    setBrushSize,
    setDrawingMode,
    setReady,
    leaveRoom,
    clearDrawing,
    resetGame,
  } = useGameStore();

  const [showSettings, setShowSettings] = useState(false);
  const [showPlayerList, setShowPlayerList] = useState(false);
  const [showChat, setShowChat] = useState(true);
  const [isReady, setIsReady] = useState(false);
  const timeIntervalRef = useRef(null);

  const colors = [
    '#000000', '#FF0000', '#00FF00', '#0000FF', '#FFFF00',
    '#FF00FF', '#00FFFF', '#FFA500', '#800080', '#008000'
  ];

  const brushSizes = [2, 4, 6, 8, 12, 16];

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    // Connect to socket
    socketManager.connect(token);

    // Set up socket event listeners
    const handleRoomJoined = (data) => {
      toast.success('Joined room successfully!');
    };

    const handlePlayerJoined = (data) => {
      toast.success(`${data.username} joined the room`);
    };

    const handlePlayerLeft = (data) => {
      toast.info(`${data.username} left the room`);
    };

    const handleGameStarted = (data) => {
      toast.success('Game started!');
    };

    const handleWordGuessed = (data) => {
      toast.success(`${data.username} guessed the word correctly!`);
    };

    const handleRoundEnded = (data) => {
      toast.info('Round ended!');
    };

    const handleGameEnded = (data) => {
      toast.success('Game ended!');
    };

    socketManager.on('room_joined', handleRoomJoined);
    socketManager.on('player_joined', handlePlayerJoined);
    socketManager.on('player_left', handlePlayerLeft);
    socketManager.on('game_started', handleGameStarted);
    socketManager.on('word_guessed', handleWordGuessed);
    socketManager.on('round_ended', handleRoundEnded);
    socketManager.on('game_ended', handleGameEnded);

    return () => {
      socketManager.off('room_joined', handleRoomJoined);
      socketManager.off('player_joined', handlePlayerJoined);
      socketManager.off('player_left', handlePlayerLeft);
      socketManager.off('game_started', handleGameStarted);
      socketManager.off('word_guessed', handleWordGuessed);
      socketManager.off('round_ended', handleRoundEnded);
      socketManager.off('game_ended', handleGameEnded);
    };
  }, [token, navigate]);

  useEffect(() => {
    // Start timer countdown
    if (isGameActive && timeRemaining > 0) {
      timeIntervalRef.current = setInterval(() => {
        // Timer logic would be handled by the game store
      }, 1000);
    }

    return () => {
      if (timeIntervalRef.current) {
        clearInterval(timeIntervalRef.current);
      }
    };
  }, [isGameActive, timeRemaining]);

  const handleLeaveRoom = () => {
    leaveRoom();
    socketManager.disconnect();
    navigate('/dashboard');
  };

  const handleStartGame = async () => {
    try {
      // Start game logic would be implemented here
      toast.success('Game started!');
    } catch (error) {
      toast.error('Failed to start game');
    }
  };

  const handleReadyToggle = () => {
    const newReadyState = !isReady;
    setIsReady(newReadyState);
    setReady(newReadyState);
    toast.success(newReadyState ? 'You are ready!' : 'You are not ready');
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <Button
                variant="outline"
                size="sm"
                onClick={handleLeaveRoom}
                className="flex items-center space-x-2"
              >
                <ArrowLeft className="w-4 h-4" />
                <span>Leave Room</span>
              </Button>

              <div className="border-l border-gray-300 h-6 mx-2" />

              <h1 className="text-xl font-semibold text-gray-900">
                {currentRoom?.name || 'Game Room'}
              </h1>

              {currentRoom?.room_code && (
                <div className="flex items-center space-x-2">
                  <span className="text-sm text-gray-600">Code:</span>
                  <code className="bg-gray-100 px-2 py-1 rounded text-sm font-mono">
                    {currentRoom.room_code}
                  </code>
                </div>
              )}
            </div>

            <div className="flex items-center space-x-4">
              {isGameActive && (
                <div className="flex items-center space-x-2">
                  <Timer className="w-5 h-5 text-red-500" />
                  <span className="text-lg font-mono text-red-600">
                    {formatTime(timeRemaining)}
                  </span>
                </div>
              )}

              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowPlayerList(!showPlayerList)}
                className="flex items-center space-x-2"
              >
                <Users className="w-4 h-4" />
                <span>Players ({players.length})</span>
              </Button>

              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowChat(!showChat)}
                className="flex items-center space-x-2"
              >
                <MessageCircle className="w-4 h-4" />
                <span>Chat</span>
              </Button>

              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowSettings(!showSettings)}
                className="flex items-center space-x-2"
              >
                <Settings className="w-4 h-4" />
                <span>Settings</span>
              </Button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Main Game Area */}
          <div className="lg:col-span-3">
            {/* Game Info */}
            {isGameActive && (
              <div className="card p-4 mb-6">
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">
                      Round {currentRound} of {totalRounds}
                    </h3>
                    {currentWord && !isDrawing && (
                      <p className="text-sm text-gray-600">
                        Word: <span className="font-mono bg-gray-100 px-2 py-1 rounded">
                          {currentWord}
                        </span>
                      </p>
                    )}
                  </div>

                  <div className="flex items-center space-x-2">
                    {currentDrawer && (
                      <span className="text-sm text-gray-600">
                        Drawing: <span className="font-medium">{currentDrawer.username}</span>
                      </span>
                    )}
                  </div>
                </div>
              </div>
            )}

            {/* Drawing Canvas */}
            <div className="card p-6">
              <DrawingCanvas width={800} height={600} />
            </div>

            {/* Drawing Tools */}
            {isDrawingMode && (
              <div className="card p-4 mt-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-2">
                      <Palette className="w-5 h-5 text-gray-600" />
                      <span className="text-sm font-medium text-gray-700">Colors:</span>
                    </div>
                    <div className="flex space-x-2">
                      {colors.map((color) => (
                        <button
                          key={color}
                          onClick={() => setBrushColor(color)}
                          className={`w-8 h-8 rounded-full border-2 ${
                            brushColor === color ? 'border-gray-900' : 'border-gray-300'
                          }`}
                          style={{ backgroundColor: color }}
                        />
                      ))}
                    </div>
                  </div>

                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-2">
                      <span className="text-sm font-medium text-gray-700">Size:</span>
                      <select
                        value={brushSize}
                        onChange={(e) => setBrushSize(parseInt(e.target.value))}
                        className="input w-20"
                      >
                        {brushSizes.map((size) => (
                          <option key={size} value={size}>
                            {size}
                          </option>
                        ))}
                      </select>
                    </div>

                    <Button
                      variant="outline"
                      size="sm"
                      onClick={clearDrawing}
                      className="flex items-center space-x-2"
                    >
                      <RotateCcw className="w-4 h-4" />
                      <span>Clear</span>
                    </Button>
                  </div>
                </div>
              </div>
            )}

            {/* Game Controls */}
            {!isGameActive && (
              <div className="card p-4 mt-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <Button
                      onClick={handleReadyToggle}
                      variant={isReady ? 'success' : 'outline'}
                      className="flex items-center space-x-2"
                    >
                      {isReady ? 'Ready' : 'Not Ready'}
                    </Button>
                  </div>

                  <Button
                    onClick={handleStartGame}
                    disabled={players.filter(p => p.is_ready).length < 2}
                    className="flex items-center space-x-2"
                  >
                    <Play className="w-4 h-4" />
                    <span>Start Game</span>
                  </Button>
                </div>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Player List */}
            {showPlayerList && (
              <motion.div
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
              >
                <PlayerList />
              </motion.div>
            )}

            {/* Chat Box */}
            {showChat && (
              <motion.div
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
              >
                <ChatBox />
              </motion.div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default GameRoom;
