import React, { useState, useRef, useEffect } from 'react';
import { Send, MessageCircle, Wifi, WifiOff } from 'lucide-react';
import useGameStore from '../store/gameStore';
import Button from './Button';

const ChatBox = () => {
  const [message, setMessage] = useState('');
  const messagesEndRef = useRef(null);

  const {
    chatMessages,
    guessInput,
    setGuessInput,
    sendChatMessage,
    sendGuess,
    isGameActive,
    currentWord,
    isDrawing,
    isSocketConnected,
  } = useGameStore();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [chatMessages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && isSocketConnected) {
      sendChatMessage(message.trim());
      setMessage('');
    } else if (!isSocketConnected) {
      console.error('Cannot send message: Socket not connected');
    }
  };

  const handleSendGuess = (e) => {
    e.preventDefault();
    if (guessInput.trim() && isSocketConnected) {
      sendGuess();
    } else if (!isSocketConnected) {
      console.error('Cannot send guess: Socket not connected');
    }
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="card h-96 flex flex-col">
      <div className="flex items-center justify-between p-4 border-b border-gray-200">
        <div className="flex items-center space-x-2">
          <MessageCircle className="w-5 h-5 text-primary-600" />
          <h3 className="font-semibold text-gray-900">Chat</h3>
          {isSocketConnected ? (
            <Wifi className="w-4 h-4 text-green-500" />
          ) : (
            <WifiOff className="w-4 h-4 text-red-500" />
          )}
        </div>
        {isGameActive && (
          <div className="text-sm text-gray-600">
            {isDrawing ? 'Drawing mode' : 'Guessing mode'}
          </div>
        )}
      </div>

      {/* Connection Status */}
      {!isSocketConnected && (
        <div className="bg-yellow-50 border-l-4 border-yellow-400 p-3">
          <div className="flex items-center">
            <WifiOff className="w-4 h-4 text-yellow-400 mr-2" />
            <p className="text-sm text-yellow-700">
              Connecting to chat server...
            </p>
          </div>
        </div>
      )}

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {chatMessages.length === 0 ? (
          <div className="text-center text-gray-500 py-8">
            <MessageCircle className="w-8 h-8 mx-auto mb-2 opacity-50" />
            <p>No messages yet. Start the conversation!</p>
          </div>
        ) : (
          chatMessages.map((msg, index) => (
            <div key={index} className="flex flex-col">
              <div className="flex items-center space-x-2 mb-1">
                <span className="font-medium text-sm text-primary-600">
                  {msg.username}
                </span>
                <span className="text-xs text-gray-500">
                  {formatTime(msg.timestamp)}
                </span>
              </div>
              <div className="bg-gray-100 rounded-lg px-3 py-2 max-w-xs">
                <p className="text-sm text-gray-900">{msg.message}</p>
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="p-4 border-t border-gray-200">
        {isGameActive && !isDrawing ? (
          // Guess input during game
          <form onSubmit={handleSendGuess} className="flex space-x-2">
            <input
              type="text"
              value={guessInput}
              onChange={(e) => setGuessInput(e.target.value)}
              placeholder={isSocketConnected ? "Type your guess..." : "Connecting..."}
              className="flex-1 input text-sm"
              maxLength={50}
              disabled={!isSocketConnected}
            />
            <Button 
              type="submit" 
              size="sm" 
              disabled={!guessInput.trim() || !isSocketConnected}
            >
              <Send className="w-4 h-4" />
            </Button>
          </form>
        ) : (
          // Regular chat input
          <form onSubmit={handleSendMessage} className="flex space-x-2">
            <input
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder={isSocketConnected ? "Type a message..." : "Connecting..."}
              className="flex-1 input text-sm"
              maxLength={200}
              disabled={!isSocketConnected}
            />
            <Button 
              type="submit" 
              size="sm" 
              disabled={!message.trim() || !isSocketConnected}
            >
              <Send className="w-4 h-4" />
            </Button>
          </form>
        )}
      </div>
    </div>
  );
};

export default ChatBox;
