import { create } from 'zustand';
import socketManager from '../utils/socket';

const useGameStore = create((set, get) => ({
  // Room state
  currentRoom: null,
  players: [],
  isInRoom: false,

  // Game state
  gameState: null,
  isGameActive: false,
  currentRound: 0,
  totalRounds: 5,
  timeRemaining: 0,
  currentWord: '',
  isDrawing: false,
  currentDrawer: null,

  // Drawing state
  drawingData: [],
  brushColor: '#000000',
  brushSize: 2,
  isDrawingMode: false,

  // Chat state
  chatMessages: [],
  guessInput: '',

  // Socket state
  isSocketConnected: false,

  // UI state
  isLoading: false,
  error: null,

  // Actions
  setSocketConnected: (connected) => {
    set({ isSocketConnected: connected });
  },

  joinRoom: (roomId) => {
    set({ isLoading: true, error: null });
    socketManager.joinRoom(roomId);
    set({ isInRoom: true, isLoading: false });
  },

  leaveRoom: () => {
    socketManager.leaveRoom();
    set({
      currentRoom: null,
      players: [],
      isInRoom: false,
      gameState: null,
      isGameActive: false,
      chatMessages: [],
      drawingData: [],
    });
  },

  setRoomData: (roomData) => {
    set({ currentRoom: roomData });
  },

  setPlayers: (players) => {
    set({ players });
  },

  addPlayer: (player) => {
    set((state) => ({
      players: [...state.players, player],
    }));
  },

  removePlayer: (playerId) => {
    set((state) => ({
      players: state.players.filter((p) => p.id !== playerId),
    }));
  },

  setGameState: (gameState) => {
    set({ gameState, isGameActive: true });
  },

  setRoundInfo: (roundInfo) => {
    set({
      currentRound: roundInfo.round,
      totalRounds: roundInfo.totalRounds,
      timeRemaining: roundInfo.timeRemaining,
      currentWord: roundInfo.word || '',
      currentDrawer: roundInfo.drawer,
      isDrawing: roundInfo.isDrawing || false,
    });
  },

  updateTimeRemaining: (time) => {
    set({ timeRemaining: time });
  },

  addDrawingData: (data) => {
    set((state) => ({
      drawingData: [...state.drawingData, data],
    }));
  },

  clearDrawing: () => {
    set({ drawingData: [] });
  },

  setBrushColor: (color) => {
    set({ brushColor: color });
  },

  setBrushSize: (size) => {
    set({ brushSize: size });
  },

  setDrawingMode: (mode) => {
    set({ isDrawingMode: mode });
  },

  addChatMessage: (message) => {
    set((state) => ({
      chatMessages: [...state.chatMessages, message],
    }));
  },

  addChatMessageFromSocket: (messageData) => {
    set((state) => ({
      chatMessages: [...state.chatMessages, messageData],
    }));
  },

  setGuessInput: (input) => {
    set({ guessInput: input });
  },

  sendGuess: () => {
    const { guessInput, isSocketConnected } = get();
    if (guessInput.trim() && isSocketConnected) {
      socketManager.sendGuess({ guess: guessInput.trim() });
      set({ guessInput: '' });
    } else if (!isSocketConnected) {
      console.error('Cannot send guess: Socket not connected');
    }
  },

  sendChatMessage: (message) => {
    const { isSocketConnected } = get();
    if (isSocketConnected) {
      socketManager.sendChatMessage({ message });
    } else {
      console.error('Cannot send chat message: Socket not connected');
    }
  },

  sendDrawData: (drawData) => {
    const { isSocketConnected } = get();
    if (isSocketConnected) {
      socketManager.sendDrawData(drawData);
    } else {
      console.error('Cannot send draw data: Socket not connected');
    }
  },

  setReady: (ready) => {
    const { isSocketConnected } = get();
    if (isSocketConnected) {
      socketManager.setReady(ready);
    } else {
      console.error('Cannot set ready status: Socket not connected');
    }
  },

  setError: (error) => {
    set({ error });
  },

  clearError: () => {
    set({ error: null });
  },

  resetGame: () => {
    set({
      gameState: null,
      isGameActive: false,
      currentRound: 0,
      timeRemaining: 0,
      currentWord: '',
      isDrawing: false,
      currentDrawer: null,
      drawingData: [],
      chatMessages: [],
      guessInput: '',
    });
  },
}));

export default useGameStore;
