import io from 'socket.io-client';

class SocketManager {
  constructor() {
    this.socket = null;
    this.isConnected = false;
    this.eventListeners = new Map();
    this.connectionAttempts = 0;
    this.maxReconnectAttempts = 5;
    this.reconnectDelay = 1000;
  }

  connect(token) {
    if (this.socket && this.isConnected) {
      console.log('Socket already connected');
      return;
    }

    console.log('Connecting to socket server...');

    this.socket = io('http://localhost:8001', {
      transports: ['websocket', 'polling'],
      timeout: 10000,
      reconnection: true,
      reconnectionAttempts: this.maxReconnectAttempts,
      reconnectionDelay: this.reconnectDelay,
      auth: {
        token: token
      }
    });

    this.socket.on('connect', () => {
      console.log('‚úÖ Connected to socket server');
      this.isConnected = true;
      this.connectionAttempts = 0;

      // Notify store about connection
      this.emit('socket_connected', { connected: true });

      // Authenticate after connection
      if (token) {
        console.log('Authenticating with token...');
        this.socket.emit('authenticate', { token });
      }
    });

    this.socket.on('disconnect', (reason) => {
      console.log('‚ùå Disconnected from socket server:', reason);
      this.isConnected = false;

      // Notify store about disconnection
      this.emit('socket_connected', { connected: false });

      if (reason === 'io server disconnect') {
        // the disconnection was initiated by the server, you need to reconnect manually
        console.log('Server disconnected, attempting to reconnect...');
        this.socket.connect();
      }
    });

    this.socket.on('connect_error', (error) => {
      console.error('‚ùå Socket connection error:', error);
      this.isConnected = false;
      this.connectionAttempts++;

      // Notify store about connection error
      this.emit('socket_connected', { connected: false, error: error.message });

      if (this.connectionAttempts < this.maxReconnectAttempts) {
        console.log(`Retrying connection (${this.connectionAttempts}/${this.maxReconnectAttempts})...`);
      } else {
        console.error('Max reconnection attempts reached');
      }
    });

    this.socket.on('error', (error) => {
      console.error('‚ùå Socket error:', error);
    });

    // Set up event listeners
    this.setupEventListeners();
  }

  disconnect() {
    if (this.socket) {
      console.log('Disconnecting from socket server...');
      this.socket.disconnect();
      this.socket = null;
      this.isConnected = false;
      this.connectionAttempts = 0;

      // Notify store about disconnection
      this.emit('socket_connected', { connected: false });
    }
  }

  setupEventListeners() {
    // Authentication events
    this.socket.on('authenticated', (data) => {
      console.log('‚úÖ Authentication successful:', data);
      this.emit('authenticated', data);
    });

    // Room events
    this.socket.on('room_joined', (data) => {
      console.log('‚úÖ Joined room:', data);
      this.emit('room_joined', data);
    });

    this.socket.on('player_joined', (data) => {
      console.log('üë§ Player joined:', data);
      this.emit('player_joined', data);
    });

    this.socket.on('player_left', (data) => {
      console.log('üë§ Player left:', data);
      this.emit('player_left', data);
    });

    this.socket.on('player_disconnected', (data) => {
      console.log('üë§ Player disconnected:', data);
      this.emit('player_disconnected', data);
    });

    // Game events
    this.socket.on('draw_data', (data) => {
      this.emit('draw_data', data);
    });

    this.socket.on('chat_message', (data) => {
      console.log('üí¨ Received chat message:', data);
      this.emit('chat_message', data);
    });

    this.socket.on('guess_result', (data) => {
      console.log('üéØ Guess result:', data);
      this.emit('guess_result', data);
    });

    this.socket.on('word_guessed', (data) => {
      console.log('üéâ Word guessed:', data);
      this.emit('word_guessed', data);
    });

    this.socket.on('player_ready', (data) => {
      console.log('‚úÖ Player ready:', data);
      this.emit('player_ready', data);
    });

    this.socket.on('game_started', (data) => {
      console.log('üéÆ Game started:', data);
      this.emit('game_started', data);
    });

    this.socket.on('round_started', (data) => {
      console.log('üîÑ Round started:', data);
      this.emit('round_started', data);
    });

    this.socket.on('round_ended', (data) => {
      console.log('‚èπÔ∏è Round ended:', data);
      this.emit('round_ended', data);
    });

    this.socket.on('game_ended', (data) => {
      console.log('üèÅ Game ended:', data);
      this.emit('game_ended', data);
    });

    this.socket.on('game_state', (data) => {
      console.log('üéÆ Game state update:', data);
      this.emit('game_state', data);
    });
  }

  // Event listener management
  on(event, callback) {
    if (!this.eventListeners.has(event)) {
      this.eventListeners.set(event, []);
    }
    this.eventListeners.get(event).push(callback);
  }

  off(event, callback) {
    if (this.eventListeners.has(event)) {
      const listeners = this.eventListeners.get(event);
      const index = listeners.indexOf(callback);
      if (index > -1) {
        listeners.splice(index, 1);
      }
    }
  }

  emit(event, data) {
    if (this.eventListeners.has(event)) {
      this.eventListeners.get(event).forEach(callback => {
        try {
          callback(data);
        } catch (error) {
          console.error(`Error in event handler for ${event}:`, error);
        }
      });
    }
  }

  // Socket event emitters
  joinRoom(roomId) {
    if (this.socket && this.isConnected) {
      console.log('Joining room:', roomId);
      this.socket.emit('join_room', { room_id: roomId });
    } else {
      console.error('Cannot join room: Socket not connected');
    }
  }

  leaveRoom() {
    if (this.socket && this.isConnected) {
      console.log('Leaving room');
      this.socket.emit('leave_room');
    } else {
      console.error('Cannot leave room: Socket not connected');
    }
  }

  sendDrawData(drawData) {
    if (this.socket && this.isConnected) {
      this.socket.emit('draw', drawData);
    } else {
      console.error('Cannot send draw data: Socket not connected');
    }
  }

  sendChatMessage(message) {
    if (this.socket && this.isConnected) {
      console.log('üí¨ Sending chat message:', message);
      this.socket.emit('chat_message', message);
    } else {
      console.error('‚ùå Cannot send chat message: Socket not connected');
    }
  }

  sendGuess(guess) {
    if (this.socket && this.isConnected) {
      console.log('üéØ Sending guess:', guess);
      this.socket.emit('guess_word', guess);
    } else {
      console.error('Cannot send guess: Socket not connected');
    }
  }

  setReady(ready) {
    if (this.socket && this.isConnected) {
      console.log('Setting ready status:', ready);
      this.socket.emit('ready', { ready });
    } else {
      console.error('Cannot set ready status: Socket not connected');
    }
  }

  startGame(roomId) {
    if (this.socket && this.isConnected) {
      console.log('Starting game for room:', roomId);
      this.socket.emit('start_game', { room_id: roomId });
    } else {
      console.error('Cannot start game: Socket not connected');
    }
  }
}

// Create singleton instance
const socketManager = new SocketManager();
export default socketManager;
