import React, { useRef, useEffect, useState } from 'react';
import useGameStore from '../store/gameStore';
import useAuthStore from '../store/authStore';

const DrawingCanvas = ({ width = 800, height = 600 }) => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [lastX, setLastX] = useState(0);
  const [lastY, setLastY] = useState(0);

  const { user } = useAuthStore();
  const {
    isDrawingMode,
    brushColor,
    brushSize,
    drawingData,
    addDrawingData,
    sendDrawData,
    currentDrawer,
    isGameActive,
    gameState,
  } = useGameStore();

  // Check if current user is the drawer
  const isCurrentDrawer = gameState?.drawer_id === user?.id;

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    canvas.width = width;
    canvas.height = height;

    // Set default styles
    ctx.fillStyle = 'white';
    ctx.fillRect(0, 0, width, height);
    ctx.lineCap = 'round';
    ctx.lineJoin = 'round';
  }, [width, height]);

  // Draw received drawing data
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    drawingData.forEach((data) => {
      if (data.is_drawing) {
        ctx.strokeStyle = data.color;
        ctx.lineWidth = data.brush_size;
        ctx.beginPath();
        ctx.moveTo(data.x, data.y);
        ctx.lineTo(data.x, data.y);
        ctx.stroke();
      }
    });
  }, [drawingData]);

  const getMousePos = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    return {
      x: e.clientX - rect.left,
      y: e.clientY - rect.top,
    };
  };

  const getTouchPos = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const touch = e.touches[0];
    return {
      x: touch.clientX - rect.left,
      y: touch.clientY - rect.top,
    };
  };

  const startDrawing = (x, y) => {
    if (!isGameActive || !isCurrentDrawer) return;

    setIsDrawing(true);
    setLastX(x);
    setLastY(y);
  };

  const draw = (x, y) => {
    if (!isDrawing || !isGameActive || !isCurrentDrawer) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    ctx.strokeStyle = brushColor;
    ctx.lineWidth = brushSize;
    ctx.beginPath();
    ctx.moveTo(lastX, lastY);
    ctx.lineTo(x, y);
    ctx.stroke();

    // Send drawing data to server
    const drawData = {
      x,
      y,
      is_drawing: true,
      color: brushColor,
      brush_size: brushSize,
    };

    sendDrawData(drawData);
    addDrawingData(drawData);

    setLastX(x);
    setLastY(y);
  };

  const stopDrawing = () => {
    if (!isGameActive || !isCurrentDrawer) return;

    setIsDrawing(false);

    // Send stop drawing signal
    const drawData = {
      x: lastX,
      y: lastY,
      is_drawing: false,
      color: brushColor,
      brush_size: brushSize,
    };

    sendDrawData(drawData);
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = 'white';
    ctx.fillRect(0, 0, width, height);
  };

  // Mouse events
  const handleMouseDown = (e) => {
    const pos = getMousePos(e);
    startDrawing(pos.x, pos.y);
  };

  const handleMouseMove = (e) => {
    const pos = getMousePos(e);
    draw(pos.x, pos.y);
  };

  const handleMouseUp = () => {
    stopDrawing();
  };

  const handleMouseLeave = () => {
    stopDrawing();
  };

  // Touch events
  const handleTouchStart = (e) => {
    e.preventDefault();
    const pos = getTouchPos(e);
    startDrawing(pos.x, pos.y);
  };

  const handleTouchMove = (e) => {
    e.preventDefault();
    const pos = getTouchPos(e);
    draw(pos.x, pos.y);
  };

  const handleTouchEnd = (e) => {
    e.preventDefault();
    stopDrawing();
  };

  return (
    <div className="relative">
      <canvas
        ref={canvasRef}
        className={`border-2 border-gray-300 rounded-lg bg-white ${
          isCurrentDrawer && isGameActive ? 'cursor-crosshair' : 'cursor-not-allowed'
        }`}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseLeave}
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
        style={{
          width: `${width}px`,
          height: `${height}px`,
          maxWidth: '100%',
          maxHeight: '100%',
        }}
      />

      {!isGameActive && (
        <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50 rounded-lg">
          <p className="text-white text-lg font-medium">
            Game not started yet
          </p>
        </div>
      )}

      {isGameActive && !isCurrentDrawer && (
        <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-30 rounded-lg">
          <p className="text-white text-lg font-medium">
            {gameState?.drawer_id ? 
              `Waiting for ${gameState.drawer_id === user?.id ? 'you' : 'someone'} to draw...` : 
              'Waiting for drawer...'
            }
          </p>
        </div>
      )}
    </div>
  );
};

export default DrawingCanvas;
